public interface HasCancelActionHandler extends HasHandlers {
  HandlerRegistration addCancelActionHandler(CancelActionHandler handler);
}
// [...]
public class ChildView extends Composite implements HasCancelActionHandlers {

  HandlerRegistration addCancelActionHandler(CancelActionHandler handler) {
    return this.addHandler(handler, CancelActionEvent.TYPE);
  }
}
// [...]
public ParentView() {
  ChildView myView = new ChildView();
  myView.addCancelActionHandler(new CancelActionHandler() {
    public void onCancelAction(CancelActionEvent event) {
      // Gestion de l'évènement
    }
  });
}
